import React, { useState, useEffect, useCallback } from 'react';
import libreApiService from '../services/libreApi';
import GlucoseDisplay from './GlucoseDisplay';
import GlucoseChart from './GlucoseChart';
import { GlucoseReading, LibrePatient, LibreConnection } from '../types/libre';
import { generateDemoGlucoseData, generateCurrentDemoReading, demoPatient, demoConnections } from '../services/demoData';

const Dashboard: React.FC = () => {
  const [currentReading, setCurrentReading] = useState<GlucoseReading | null>(null);
  const [glucoseHistory, setGlucoseHistory] = useState<GlucoseReading[]>([]);
  const [patient, setPatient] = useState<LibrePatient | null>(null);
  const [connections, setConnections] = useState<LibreConnection[]>([]);
  const [selectedConnection, setSelectedConnection] = useState<string>('');
  const [timeRange, setTimeRange] = useState<'1h' | '6h' | '12h' | '24h'>('24h');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [autoRefresh, setAutoRefresh] = useState(true);
  // Nightscout integration enabled - using real data
  const [nightscoutUrl] = useState(process.env.REACT_APP_NIGHTSCOUT_URL || '');

  // Debug: Log environment variables
  console.log('Environment variables:', {
    NIGHTSCOUT_URL: process.env.REACT_APP_NIGHTSCOUT_URL,
    NIGHTSCOUT_SECRET: process.env.REACT_APP_NIGHTSCOUT_SECRET,
    ENABLE_DEMO: process.env.REACT_APP_ENABLE_DEMO_MODE
  });
  console.log('Nightscout URL state:', nightscoutUrl);

  // Helper functions for Nightscout data conversion
  const convertTrendToArrow = (direction: string): string => {
    const trendMap: { [key: string]: string } = {
      'DoubleUp': '↗↗',
      'SingleUp': '↗',
      'FortyFiveUp': '↗',
      'Flat': '→',
      'FortyFiveDown': '↘',
      'SingleDown': '↘',
      'DoubleDown': '↘↘',
      'NOT COMPUTABLE': '→',
      'RATE OUT OF RANGE': '→',
    };
    return trendMap[direction] || '→';
  };

  const calculateGlucoseStatus = (value: number): 'low' | 'normal' | 'high' | 'critical' => {
    if (value < 70) return 'low';
    if (value < 180) return 'normal';
    if (value < 250) return 'high';
    return 'critical';
  };

  const fetchPatientInfo = useCallback(async () => {
    // Temporarily disable authentication - always use demo data
    setPatient(demoPatient);
  }, []);

  const fetchConnections = useCallback(async () => {
    // Temporarily disable authentication - always use demo data
    setConnections(demoConnections);
    if (demoConnections.length > 0 && !selectedConnection) {
      setSelectedConnection(demoConnections[0].id);
    }
  }, [selectedConnection]);

  const fetchCurrentGlucose = useCallback(async () => {
    if (!selectedConnection) return;

    setIsLoading(true);
    setError(null);

    // Use Nightscout data if available, otherwise fall back to demo
    if (nightscoutUrl) {
      try {
        console.log('🔍 Attempting to fetch from Nightscout:', nightscoutUrl);
        const response = await fetch(`${nightscoutUrl}/api/v2/entries.json?count=1`, {
          headers: {
            'Content-Type': 'application/json',
          },
        });

        if (response.ok) {
          const data = await response.json();
          if (data.length > 0) {
            const entry = data[0];
            const reading = {
              timestamp: new Date(entry.date),
              value: entry.sgv,
              trend: entry.trend || 0,
              trendArrow: convertTrendToArrow(entry.direction),
              status: calculateGlucoseStatus(entry.sgv),
              unit: 'mg/dL',
            };

            setCurrentReading(reading);
            setGlucoseHistory(prev => {
              const newHistory = [...prev, reading];
              return newHistory.slice(-100);
            });
          }
        } else {
          throw new Error('Failed to fetch from Nightscout');
        }
      } catch (err) {
        console.error('Nightscout fetch failed, using demo data:', err);
        // Fall back to demo data
        console.log('🔄 Falling back to demo data for current reading');
        const reading = generateCurrentDemoReading();
        setCurrentReading(reading);
        setGlucoseHistory(prev => {
          const newHistory = [...prev, reading];
          return newHistory.slice(-100);
        });
      }
          } else {
        // Use demo data if Nightscout not configured
        console.log('🔄 Using demo data - Nightscout not configured');
        const reading = generateCurrentDemoReading();
      setCurrentReading(reading);
      setGlucoseHistory(prev => {
        const newHistory = [...prev, reading];
        return newHistory.slice(-100);
      });
    }

    setIsLoading(false);
  }, [selectedConnection, nightscoutUrl]);

  const fetchHistoricalData = useCallback(async () => {
    if (!selectedConnection) return;

    // Use Nightscout data if available, otherwise fall back to demo
    if (nightscoutUrl) {
      try {
        console.log('🔍 Attempting to fetch historical data from Nightscout:', nightscoutUrl);
        const endDate = new Date();
        const startDate = new Date();
        const hours = timeRange === '1h' ? 1 : timeRange === '6h' ? 6 : timeRange === '12h' ? 12 : 24;
        startDate.setHours(startDate.getHours() - hours);

        const response = await fetch(
          `${nightscoutUrl}/api/v2/entries.json?find[date][$gte]=${startDate.toISOString()}&find[date][$lte]=${endDate.toISOString()}`,
          {
            headers: {
              'Content-Type': 'application/json',
            },
          }
        );

        if (response.ok) {
          const data = await response.json();
          const history = data.map((entry: any) => ({
            timestamp: new Date(entry.date),
            value: entry.sgv,
            trend: entry.trend || 0,
            trendArrow: convertTrendToArrow(entry.direction),
            status: calculateGlucoseStatus(entry.sgv),
            unit: 'mg/dL',
          }));

          setGlucoseHistory(history);
        } else {
          throw new Error('Failed to fetch historical data from Nightscout');
        }
      } catch (err) {
        console.error('Nightscout historical fetch failed, using demo data:', err);
        // Fall back to demo data
        console.log('🔄 Falling back to demo data for historical data');
        const hours = timeRange === '1h' ? 1 : timeRange === '6h' ? 6 : timeRange === '12h' ? 12 : 24;
        const history = generateDemoGlucoseData(hours);
        setGlucoseHistory(history);
      }
    } else {
      // Use demo data if Nightscout not configured
      console.log('🔄 Using demo data for historical - Nightscout not configured');
      const hours = timeRange === '1h' ? 1 : timeRange === '6h' ? 6 : timeRange === '12h' ? 12 : 24;
      const history = generateDemoGlucoseData(hours);
      setGlucoseHistory(history);
    }
  }, [selectedConnection, timeRange, nightscoutUrl]);

  // Initial data fetch
  useEffect(() => {
    fetchPatientInfo();
    fetchConnections();

    // Load data from Nightscout if available, otherwise use demo
    if (nightscoutUrl) {
      // Fetch initial data from Nightscout
      fetchHistoricalData();
      fetchCurrentGlucose();
    } else {
      // Load demo data if Nightscout not configured
      const demoHistory = generateDemoGlucoseData(24);
      setGlucoseHistory(demoHistory);
      setCurrentReading(generateCurrentDemoReading());
    }
  }, [fetchPatientInfo, fetchConnections, nightscoutUrl, fetchHistoricalData, fetchCurrentGlucose]);

  // Fetch current glucose data
  useEffect(() => {
    if (selectedConnection) {
      fetchCurrentGlucose();
      fetchHistoricalData();
    }
  }, [selectedConnection, fetchCurrentGlucose, fetchHistoricalData]);

  // Auto-refresh every 5 minutes
  useEffect(() => {
    if (!autoRefresh || !selectedConnection) return;

    const interval = setInterval(() => {
      fetchCurrentGlucose();
    }, 5 * 60 * 1000); // 5 minutes

    return () => clearInterval(interval);
  }, [autoRefresh, selectedConnection, fetchCurrentGlucose]);

  const handleLogout = () => {
    libreApiService.logout();
    window.location.reload();
  };

  const handleConnectionChange = (connectionId: string) => {
    setSelectedConnection(connectionId);
    setGlucoseHistory([]);
  };

  const handleTimeRangeChange = (range: '1h' | '6h' | '12h' | '24h') => {
    setTimeRange(range);
    fetchHistoricalData();
  };

  if (error) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="max-w-md w-full bg-white rounded-lg shadow-lg p-6">
          <div className="text-center">
            <div className="mx-auto h-12 w-12 text-red-500 mb-4">
              <svg fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-2.5L13.732 4c-.77-.833-1.964-.833-2.732 0L3.732 16.5c-.77.833.192 2.5 1.732 2.5z" />
              </svg>
            </div>
            <h3 className="text-lg font-medium text-gray-900 mb-2">Error</h3>
            <p className="text-gray-600 mb-4">{error}</p>
            <button
              onClick={fetchCurrentGlucose}
              className="btn-primary"
            >
              Retry
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-4">
            <div className="flex items-center">
              <h1 className="text-2xl font-bold text-gray-900">Libre Glucose Monitor</h1>
              {patient && (
                <span className="ml-4 text-sm text-gray-600">
                  Welcome, {patient.firstName} {patient.lastName}
                </span>
              )}
            </div>

            <div className="flex items-center space-x-4">
              {nightscoutUrl ? (
                <div className="bg-green-100 text-green-800 px-3 py-1 rounded-full text-sm font-medium">
                  🩸 Nightscout Connected
                </div>
              ) : (
                <div className="bg-yellow-100 text-yellow-800 px-3 py-1 rounded-full text-sm font-medium">
                  🧪 Demo Mode Active
                </div>
              )}

              <div className="flex items-center space-x-2">
                <label className="text-sm text-gray-600">Auto-refresh</label>
                <input
                  type="checkbox"
                  checked={autoRefresh}
                  onChange={(e) => setAutoRefresh(e.target.checked)}
                  className="rounded border-gray-300 text-primary-600 focus:ring-primary-500"
                />
              </div>

              {connections.length > 0 && (
                <select
                  value={selectedConnection}
                  onChange={(e) => handleConnectionChange(e.target.value)}
                  className="border border-gray-300 rounded-md px-3 py-2 text-sm focus:outline-none focus:ring-2 focus:ring-primary-500"
                >
                  {connections.map(connection => (
                    <option key={connection.id} value={connection.id}>
                      Sensor {connection.id.slice(-4)}
                    </option>
                  ))}
                </select>
              )}

              <button
                onClick={handleLogout}
                className="btn-secondary"
              >
                Logout
              </button>
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {nightscoutUrl ? (
          <div className="mb-6 bg-green-50 border border-green-200 rounded-lg p-4">
            <div className="flex items-center">
              <div className="flex-shrink-0">
                <svg className="h-5 w-5 text-green-400" viewBox="0 0 20 20" fill="currentColor">
                  <path fillRule="evenodd" d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" clipRule="evenodd" />
                </svg>
              </div>
              <div className="ml-3">
                <h3 className="text-sm font-medium text-green-800">
                  🩸 Nightscout Connected - Real Data Active
                </h3>
                <div className="mt-2 text-sm text-green-700">
                  <p>Your app is now connected to Nightscout and displaying real-time glucose data from your Libre 2 Plus sensor. Data updates automatically every 5 minutes.</p>
                </div>
              </div>
            </div>
          </div>
        ) : (
          <div className="mb-6 bg-yellow-50 border border-yellow-200 rounded-lg p-4">
            <div className="flex items-center">
              <div className="flex-shrink-0">
                <svg className="h-5 w-5 text-yellow-400" viewBox="0 0 20 20" fill="currentColor">
                  <path fillRule="evenodd" d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-7-4a1 1 0 11-2 0 1 1 0 012 0zM9 9a1 1 0 000 2v3a1 1 0 001 1h1a1 1 0 100-2v-3a1 1 0 00-1-1H9z" clipRule="evenodd" />
                </svg>
              </div>
              <div className="ml-3">
                <h3 className="text-sm font-medium text-yellow-800">
                  🧪 Demo Mode Active
                </h3>
                <div className="mt-2 text-sm text-yellow-700">
                  <p>You're currently viewing sample glucose data. To connect to your Nightscout instance, check your environment variables.</p>
                </div>
              </div>
            </div>
          </div>
        )}

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* Current Glucose Display */}
          <div>
            <GlucoseDisplay 
              reading={currentReading} 
              isLoading={isLoading} 
            />
          </div>

          {/* Glucose Chart */}
          <div>
            <GlucoseChart 
              data={glucoseHistory} 
              timeRange={timeRange}
            />
          </div>
        </div>

        {/* Time Range Controls */}
        <div className="mt-8 flex justify-center">
          <div className="inline-flex rounded-lg border border-gray-200 bg-white p-1">
            {(['1h', '6h', '12h', '24h'] as const).map((range) => (
              <button
                key={range}
                onClick={() => handleTimeRangeChange(range)}
                className={`px-4 py-2 text-sm font-medium rounded-md transition-colors ${
                  timeRange === range
                    ? 'bg-primary-100 text-primary-700 border-primary-200'
                    : 'text-gray-500 hover:text-gray-700 hover:bg-gray-50'
                }`}
              >
                {range}
              </button>
            ))}
          </div>
        </div>

        {/* Status Information */}
        <div className="mt-8 grid grid-cols-1 md:grid-cols-3 gap-6">
          <div className="bg-white rounded-lg shadow p-6">
            <h3 className="text-lg font-medium text-gray-900 mb-4">Sensor Status</h3>
            <div className="space-y-3">
              <div className="flex justify-between">
                <span className="text-gray-600">Connection:</span>
                <span className="font-medium text-gray-900">
                  {selectedConnection ? 'Connected' : 'Disconnected'}
                </span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-600">Last Sync:</span>
                <span className="font-medium text-gray-900">
                  {currentReading ? currentReading.timestamp.toLocaleTimeString() : 'Never'}
                </span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-600">Auto-refresh:</span>
                <span className="font-medium text-gray-900">
                  {autoRefresh ? 'Enabled' : 'Disabled'}
                </span>
              </div>
            </div>
          </div>

          <div className="bg-white rounded-lg shadow p-6">
            <h3 className="text-lg font-medium text-gray-900 mb-4">Glucose Ranges</h3>
            <div className="space-y-3">
              <div className="flex justify-between">
                <span className="text-gray-600">Low:</span>
                <span className="font-medium text-red-600">&lt; 70 mg/dL</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-600">Normal:</span>
                <span className="font-medium text-green-600">70-180 mg/dL</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-600">High:</span>
                <span className="font-medium text-yellow-600">180-250 mg/dL</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-600">Critical:</span>
                <span className="font-medium text-red-600">&gt; 250 mg/dL</span>
              </div>
            </div>
          </div>

          <div className="bg-white rounded-lg shadow p-6">
            <h3 className="text-lg font-medium text-gray-900 mb-4">Quick Actions</h3>
            <div className="space-y-3">
              <button
                onClick={fetchCurrentGlucose}
                disabled={isLoading}
                className="w-full btn-primary"
              >
                {isLoading ? 'Refreshing...' : 'Refresh Now'}
              </button>
              <button
                onClick={() => setAutoRefresh(!autoRefresh)}
                className={`w-full ${autoRefresh ? 'btn-secondary' : 'btn-primary'}`}
              >
                {autoRefresh ? 'Disable Auto-refresh' : 'Enable Auto-refresh'}
              </button>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
};

export default Dashboard;
